import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogoClick = (e) => {
    e.preventDefault();
    window.scrollTo({ top: 0, behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <a href="/" className="brand" onClick={handleLogoClick}>
              <img src="/images/logo.png" alt="Oceanfront Pressure Pros logo" className="logo-img" />
              <span className="brand-text">Oceanfront Pressure Pros</span>
              <img
                src="/images/marinecorp_logo.png"
                alt="United States Marine Corps emblem"
                title="Active-Duty U.S. Marineâ€“Owned"
                className="marine-logo"
              />
            </a>
          </div>

          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              <li><button onClick={() => scrollToSection('about')}>About</button></li>
              <li><button onClick={() => scrollToSection('before-after')}>Before & After</button></li>
              <li><button onClick={() => scrollToSection('reviews-page')}>Reviews</button></li>
              <li><button onClick={() => scrollToSection('contact')}>Contact</button></li>
            </ul>
          </nav>

          <div className="header-cta">
            <a href="tel:586-612-7380" className="btn btn-primary">Call Now</a>
          </div>

          <button className="mobile-menu-btn" onClick={toggleMenu}>
            <span className={`hamburger ${isMenuOpen ? 'open' : ''}`}></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
