import React from 'react';
import './Hero.css';

const Hero = () => {
  const bgStyle = {
    background: `linear-gradient(135deg, rgba(26, 54, 93, 0.6), rgba(45, 90, 135, 0.6)), url(${process.env.PUBLIC_URL}/images/marinecorp_logo.png)`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  };

  return (
    <section className="hero" style={bgStyle}>
      <div className="hero-overlay"></div>
      <div className="container">
        <div className="hero-content">
          <h1 className="hero-title">
            Professional Pressure Washing Services
            <span className="hero-subtitle">Along the Beautiful North Carolina Coast</span>
          </h1>
          <p className="hero-description">
            From house washing and driveway cleaning to decks, fences, and commercial services - 
            we bring coastal properties back to life with results that last.
          </p>
          <div className="hero-cta">
            <a href="#contact" className="btn btn-primary hero-btn">
              Get Free Estimate
            </a>
            <a href="tel:586-612-7380" className="btn btn-secondary hero-btn">
              ðŸ“ž Call 586-612-7380
            </a>
          </div>

        </div>
      </div>
    </section>
  );
};

export default Hero;
